import { createShapesClient } from "../src/client";

const SHAPES_API_KEY = process.env.SHAPES_API_KEY;
const SHAPES_APP_ID = process.env.SHAPES_APP_ID;
const SHAPES_AUTH_TOKEN = process.env.SHAPES_AUTH_TOKEN;

export const shapes_client = createShapesClient({
  apiKey: SHAPES_API_KEY,
  appId: SHAPES_APP_ID,
  authToken: SHAPES_AUTH_TOKEN,
});
