import { createShapesClient } from "./client";
import { createSendMessage } from "./chat";
import { createAnalyzeAudio } from "./audio";
import { createAnalyzeImage } from "./image";

export interface SDKOptions {
  apiKey?: string;
  appId?: string;
  authToken?: string;
  baseUrl?: string;
}

export function createShapesSDK(options: SDKOptions = {}) {
  const client = createShapesClient(options);

  return {
    sendMessage: createSendMessage(client),
    analyzeAudio: createAnalyzeAudio(client),
    analyzeImage: createAnalyzeImage(client),
  };
}
